    # 여기서 image_content는 테이플 이미지의 내용입니다 (실제로는 OCR 결과)
    table_rows = []
    for row in image_content.split('\n'):
        if 'H' not in row and len(row.strip()) > 0 :
            table_rows.append(row)

    summary_text_list=[]
    current_row= ""
    previous_value=""
    for line_num ,row_valu ein enumerate(table_rows):        
        if row_valu e.startswith('H') or row_valu endswith(':'):            
            continue

        if ':' == row_valu es[-1]:
